import React from 'react';
import './post.css'

function Post(props) {
  //the "normal" source local path  (like with regular HTML) doesn't work 
  //the localhost:5000/filename.jpg won't work unless change server to apollo-express 
      //tried doing this, had errors
  //internet suggested using <img src=equire(pathlink)/>, but this didn't work for me
  //so, i used dynamic import to import the image based on prop imageName value

  import("./images/test.png").then(test=>{
    return (
      <div className="container-post">
        <div className="post-image">
          <img src={test} alt=""/>

        </div>
        <div className="post-content">
          <h1 className="title-post">{props.title}</h1>
          <p className="paragraphs">{props.paragraph}</p>
          <h5 className="tags">Tags: {props.tags.toString()}</h5> 
          <h5 className="likes">{props.likes} likes</h5>
          <h5 className="comment-title">COMMENTS</h5>
          <p className="comments">{props.comments}</p>
        </div>
      </div>
    )
    }); 

}
  export default Post

  //"https://images.unsplash.com/photo-1498837167922-ddd27525d352?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=1080&fit=max"
